class SurveillanceExportBox
  extends 	FullBox(‘suep’, version = 1, 0){
  string  	ExportUnitName;
  string  	ExportUnitURL;
  string  	ExportUnitMAC;
  UInt(64)  	ExportUnitTime;
  string  	ExportOperator;
  UInt(32) entry_count; 	
  int i; 	
  for (i=0; i < entry_count; i++) {
    UInt(16)  	TrackID;
    string  	SourceName;
    string  	SourceURL;
    string  	SourceMAC;
    string  	SourceLine;
  }
}

